import Foundation
import ArgumentParser
import CommandLineImage

struct ITermImage: ParsableCommand {

	static let configuration = CommandConfiguration(commandName: "iterm-image", abstract: "Display an example image in iTerm format.")

	static let testImage = ""

	func run() {
		print("iTerm image example:")
		let image = CLIImage(base64String: Self.testImage)
		print(image.escapeString(width: .pixels(100)))
	}
	
}
